package exe;

import java.util.Scanner;

public class AddTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;
		Scanner s = new Scanner(System.in);
		System.out.println("Enter x and y");

		x = s.nextInt();
		y = s.nextInt();
		int z = x + y;
		System.out.println(z);

	}

}
